import React, { useState, useRef } from 'react';
import Tesseract from 'tesseract.js';
import './ScreenStyle/Scan.css'; // Import the external CSS file

const Scan = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [recognizedText, setRecognizedText] = useState('');
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const handleCameraButtonClick = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (err) {
      console.error('Error accessing camera:', err);
    }
  };

  const handleCaptureButtonClick = () => {
    const context = canvasRef.current.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
    const capturedImage = canvasRef.current.toDataURL('image/png');
    setSelectedImage(capturedImage);
  };



  return (
    <div className="scan-container">
      <h1>ScanScreen</h1>
      <div className="rectangle-grid">
        <div className="rectangle">
          <h2>⚙️ Scan...</h2>
        </div>
      </div>
      <div>
        <button className="button" onClick={handleCameraButtonClick}>Open Camera</button>
        <button className="button" onClick={handleCaptureButtonClick}>Capture</button>
        <video ref={videoRef} autoPlay={true} style={{ display: 'none' }} />
        <canvas ref={canvasRef} style={{ display: 'none' }} />
        {selectedImage && <img src={selectedImage} alt="Selected" />}
        {selectedImage && (
          <div>
            <h2>Recognized Text:</h2>
            <p>{recognizedText}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Scan;
